import React, { useState } from "react";
import UploadCV from "./components/UploadCV";
import { checkATS, matchJD } from "./api";

export default function App() {
  const [cvData, setCvData] = useState(null);
  const [atsScore, setAtsScore] = useState(null);
  const [jdScore, setJdScore] = useState(null);
  const [jobDescription, setJobDescription] = useState("");

  const handleATS = async () => {
    try {
      const res = await checkATS();
      setAtsScore(res.data.score);
    } catch (err) {
      alert("ATS check failed. Make sure backend is running.");
      console.error(err);
    }
  };

  const handleJDMatch = async () => {
    if (!jobDescription) return alert("Enter job description first.");
    try {
      const res = await matchJD(jobDescription);
      setJdScore(res.data.matchScore);
    } catch (err) {
      alert("JD match failed.");
      console.error(err);
    }
  };

  return (
    <div style={{ padding: 20 }}>
      <h1>CV ATS System</h1>

      <UploadCV setData={setCvData} />

      {cvData && (
        <div style={{ marginTop: 20 }}>
          <h2>Extracted CV Data:</h2>
          <p><strong>Name:</strong> {cvData.name}</p>
          <p><strong>Email:</strong> {cvData.email}</p>
          <p><strong>Phone:</strong> {cvData.phone}</p>

          <h3>Education:</h3>
          <ul>{cvData.education?.map((edu, i) => <li key={i}>{edu}</li>)}</ul>

          <h3>Experience:</h3>
          <ul>{cvData.experience?.map((exp, i) => <li key={i}>{exp}</li>)}</ul>

          <h3>Projects:</h3>
          <ul>{cvData.projects?.map((proj, i) => <li key={i}>{proj}</li>)}</ul>

          <div style={{ marginTop: 20 }}>
            <button onClick={handleATS}>Check ATS Score</button>
            {atsScore && <p>ATS Score: {atsScore}/100</p>}

            <div style={{ marginTop: 10 }}>
              <textarea
                rows="3"
                cols="50"
                placeholder="Paste Job Description here..."
                value={jobDescription}
                onChange={(e) => setJobDescription(e.target.value)}
              />
              <br />
              <button onClick={handleJDMatch}>Match Job Description</button>
              {jdScore && <p>JD Match Score: {jdScore}%</p>}
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
