import axios from "axios";

const API = "http://localhost:5000/api/cv";

export const uploadCV = async (file) => {
  const fd = new FormData();
  fd.append("file", file);
  return axios.post(`${API}/upload`, fd, { headers: { "Content-Type": "multipart/form-data" } });
};

export const checkATS = async () => axios.post(`${API}/ats`);
export const matchJD = async (jd) => axios.post(`${API}/match-jd`, { jobDescription: jd });
