import React, { useState } from "react";
import UploadCV from "./components/UploadCV";
import { uploadCV, matchJD } from "./api";
import {
  FaUser,
  FaEnvelope,
  FaPhone,
  FaGraduationCap,
  FaBriefcase,
  FaProjectDiagram,
  FaTools,
  FaCertificate,
  FaCheckCircle,
  FaExclamationTriangle,
} from "react-icons/fa";
import "./App.css";

export default function App() {
  const [cvData, setCvData] = useState(null);
  const [jobDescription, setJobDescription] = useState("");
  const [jdScore, setJdScore] = useState(null);
  const [loadingCV, setLoadingCV] = useState(false);
  const [loadingJD, setLoadingJD] = useState(false);

  const handleUpload = async (file) => {
    setLoadingCV(true);
    setCvData(null);
    setJdScore(null);
    try {
      const res = await uploadCV(file);
      if (!res?.data?.parsedData) throw new Error("Invalid CV response");
      const data = res.data.parsedData;

      // Safe phone separation
      if (data.phone) {
        const phones = data.phone.match(/\+?\d[\d\s]{6,}/g);
        data.phone = phones && phones.length > 0 ? phones.join(" | ") : data.phone;
      }

      setCvData({ ...data, rawText: res.data.rawText || "" });
    } catch (err) {
      alert("CV upload failed");
      console.error(err);
    } finally {
      setLoadingCV(false);
    }
  };

  const handleJDMatch = async () => {
    if (!cvData || !jobDescription) return alert("Paste job description first");
    setLoadingJD(true);
    setJdScore(null);

    try {
      const res = await matchJD({ rawText: cvData.rawText, jobDescription });
      if (!res?.data?.matchPercentage && res.data.matchPercentage !== 0) throw new Error("JD match failed");
      setJdScore(res.data.matchPercentage);
    } catch (err) {
      alert("JD matching failed");
      console.error(err);
    } finally {
      setLoadingJD(false);
    }
  };

  return (
    <div className="container app-container">
      <h1 className="neon-title">CV ATS System</h1>

      <UploadCV setData={handleUpload} />
      {loadingCV && <p className="loading-text">Processing CV...</p>}

      {cvData && (
        <div className="glass-card">
          {/* Basic Info */}
          <div className="basic-info">
            <div><FaUser /> {cvData.name || "N/A"}</div>
            <div><FaEnvelope /> {cvData.email || "N/A"}</div>
            <div><FaPhone /> {cvData.phone || "N/A"}</div>
          </div>

          {/* Main Grid */}
          <div className="grid-layout">
            {/* Education */}
            <div className="card-section">
              <h3><FaGraduationCap /> Education</h3>
              <ul>
                {cvData.education?.length
                  ? cvData.education.map((edu, i) => (
                      <li key={i}>
                        <strong>{edu.degree}</strong> at {edu.institution} ({edu.start_date || "-"} - {edu.end_date || "-"})<br/>
                        Field: {edu.field_of_study || "-"}, GPA: {edu.gpa || "-"}<br/>
                        {edu.details || ""}
                      </li>
                    ))
                  : <li>N/A</li>}
              </ul>
            </div>

            {/* Experience */}
            <div className="card-section">
              <h3><FaBriefcase /> Experience</h3>
              <ul>
                {cvData.experience?.length
                  ? cvData.experience.map((exp, i) => (
                      <li key={i}>
                        <strong>{exp.title}</strong> at {exp.company} ({exp.start_date || "-"} - {exp.end_date || "-"})<br/>
                        {exp.description || ""}
                      </li>
                    ))
                  : <li>N/A</li>}
              </ul>
            </div>

            {/* Projects */}
            <div className="card-section">
              <h3><FaProjectDiagram /> Projects</h3>
              <ul>
                {cvData.projects?.length
                  ? cvData.projects.map((p, i) => (
                      <li key={i}><strong>{p.title}</strong>: {p.description}</li>
                    ))
                  : <li>N/A</li>}
              </ul>
            </div>

            {/* Skills */}
            <div className="card-section">
              <h3><FaTools /> Skills</h3>
              <ul className="tag-list">
                {cvData.skills?.length
                  ? cvData.skills.map((s, i) => <li key={i}>{typeof s === "string" ? s : s.name}</li>)
                  : <li>N/A</li>}
              </ul>
            </div>

            {/* Certifications */}
            <div className="card-section">
              <h3><FaCertificate /> Certifications</h3>
              <ul>
                {cvData.certifications?.length
                  ? cvData.certifications.map((c, i) => <li key={i}>{typeof c === "string" ? c : c.name}</li>)
                  : <li>N/A</li>}
              </ul>
            </div>
          </div>

          {/* Highlights */}
          <div className="glass-card">
            <div className="analysis-wrapper">
              <div className="highlight success">
                <h3><FaCheckCircle /> Strong Highlights</h3>
                <ul>
                  {cvData.highlights?.length ? cvData.highlights.map((h, i) => <li key={i}>{h}</li>) : <li>N/A</li>}
                </ul>
              </div>

              <div className="highlight warning">
                <h3><FaExclamationTriangle /> Needs Improvement</h3>
                <ul>
                  {cvData.improvements?.length ? cvData.improvements.map((i, idx) => <li key={idx}>{i}</li>) : <li>N/A</li>}
                </ul>
              </div>
            </div>

            <div style={{ marginTop: "25px" }}>
              <p className="neon-text">ATS Score: {cvData.atsScore ?? "N/A"}/100</p>
            </div>
          </div>

          {/* JD Match */}
          <div className="jd-box">
            <textarea
              rows="5"
              placeholder="Paste Job Description here..."
              value={jobDescription}
              onChange={(e) => setJobDescription(e.target.value)}
            />
            <button className="neon-btn" onClick={handleJDMatch}>
              {loadingJD ? "Matching..." : "Match Job Description"}
            </button>
            {jdScore !== null && !loadingJD && (
              <p className="neon-text">JD Match Score: {jdScore}%</p>
            )}
          </div>
        </div>
      )}
    </div>
  );
}
