import axios from "axios";

const API = "http://localhost:5000"; // root backend URL

// Upload CV
export const uploadCV = async (file) => {
  const fd = new FormData();
  fd.append("file", file); // Must match multer field name

  try {
    const res = await axios.post(`${API}/upload`, fd, {
      headers: {
        "Content-Type": "multipart/form-data",
      },
    });
    return res;
  } catch (error) {
    console.error("CV Upload Error:", error);
    throw error;
  }
};

// Check ATS score
export const checkATS = async () => {
  try {
    const res = await axios.post(`${API}/ats`);
    return res;
  } catch (error) {
    console.error("ATS Check Error:", error);
    throw error;
  }
};

// Match Job Description
export const matchJD = async (jd) => {
  try {
    const res = await axios.post(`${API}/match-jd`, { jobDescription: jd });
    return res;
  } catch (error) {
    console.error("JD Match Error:", error);
    throw error;
  }
};
