import { uploadCV } from "../api";
import { useState } from "react";

export default function UploadCV({ setData }) {
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleChange = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    setLoading(true);
    setError(null);
    try {
      // FormData with field name 'cv' (must match backend multer)
      const res = await uploadCV(file);
      console.log("Backend response:", res.data); // debug JSON
      setData(res.data);
    } catch (err) {
      console.error("Upload failed:", err);
      setError("Upload failed. Is backend running?");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <input type="file" onChange={handleChange} accept=".pdf,.doc,.docx" />
      {loading && <p>Uploading...</p>}
      {error && <p style={{ color: "red" }}>{error}</p>}
    </div>
  );
}
