import React, { useState } from "react";
import UploadCV from "./components/UploadCV";
import { checkATS, matchJD } from "./api";

function App() {
  const [cvData, setCvData] = useState(null);
  const [atsScore, setAtsScore] = useState(null);
  const [jdScore, setJdScore] = useState(null);
  const [jobDesc, setJobDesc] = useState("");

  return (
    <div style={{ padding: "20px", fontFamily: "Arial" }}>
      <h1>CV ATS System</h1>
      
      <UploadCV setData={setCvData} />

      {cvData && (
        <div style={{ marginTop: "20px" }}>
          <h2>Extracted CV Data:</h2>
          <p><strong>Name:</strong> {cvData.name}</p>
          <p><strong>Email:</strong> {cvData.email}</p>
          <p><strong>Phone:</strong> {cvData.phone}</p>
          <p><strong>Education:</strong> {cvData.education.length ? cvData.education.join(", ") : "N/A"}</p>
          <p><strong>Experience:</strong> {cvData.experience.length ? cvData.experience.join(", ") : "N/A"}</p>
          <p><strong>Projects:</strong> {cvData.projects.length ? cvData.projects.join(", ") : "N/A"}</p>
        </div>
      )}

      <div style={{ marginTop: "20px" }}>
        <button onClick={async () => {
          const res = await checkATS();
          setAtsScore(res.data.atsScore);
        }}>
          Check ATS Score
        </button>
        {atsScore !== null && <p>ATS Score: {atsScore}/100</p>}
      </div>

      <div style={{ marginTop: "20px" }}>
        <textarea
          placeholder="Enter job description..."
          value={jobDesc}
          onChange={(e) => setJobDesc(e.target.value)}
          style={{ width: "100%", height: "80px" }}
        />
        <button onClick={async () => {
          const res = await matchJD(jobDesc);
          setJdScore(res.data.matchScore);
        }}>
          Match Job Description
        </button>
        {jdScore !== null && <p>JD Match Score: {jdScore}%</p>}
      </div>
    </div>
  );
}

export default App;
