import axios from "axios";
const API = "http://localhost:5000/api/cv"; // make sure backend uses this prefix

export const uploadCV = async (file) => {
  const fd = new FormData();
  fd.append("file", file); // must match multer field
  return axios.post(`${API}/upload`, fd, { headers: { "Content-Type": "multipart/form-data" } });
};

export const checkATS = async () => {
  return axios.post(`${API}/ats`);
};

export const matchJD = async (jd) => {
  return axios.post(`${API}/match-jd`, { jobDescription: jd });
};
