import axios from "axios";

const API = "http://localhost:5000/api/cv";

export const uploadCV = (file) => {
  const fd = new FormData();
  fd.append("cv", file);
  return axios.post(`${API}/upload`, fd);
};

export const checkATS = () => axios.post(`${API}/ats`);
export const matchJD = (jd) => axios.post(`${API}/match-jd`, { jobDescription: jd });
