import React, { useState } from "react";
import UploadCV from "./components/UploadCV";
import { checkATS, matchJD, uploadCV as uploadCVAPI } from "./api";
import {
  FaUser,
  FaEnvelope,
  FaPhone,
  FaGraduationCap,
  FaBriefcase,
  FaProjectDiagram,
  FaTools,
  FaCertificate,
  FaCheckCircle,
  FaExclamationTriangle,
} from "react-icons/fa";
import "./App.css";

export default function App() {
  const [cvData, setCvData] = useState(null);
  const [jdScore, setJdScore] = useState(null);
  const [jobDescription, setJobDescription] = useState("");
  const [loading, setLoading] = useState(false);

  const handleUploadCV = async (file) => {
    if (!file) return;
    setLoading(true);
    try {
      const res = await uploadCVAPI(file);

      // Separate phone numbers if multiple
      let phoneNumbers = [];
      if (res.data.parsedData.phone) {
        phoneNumbers = res.data.parsedData.phone
          .replace(/\s+/g, " ")
          .split(/(?<=\d{10})\s*/); // split numbers if consecutive
      }
      res.data.parsedData.phone = phoneNumbers.join(" | ");

      // Update CV data and reset previous ATS & JD
      setCvData(res.data.parsedData);
      setJdScore(null);
    } catch (err) {
      console.error("CV upload failed:", err);
      alert("CV upload failed");
    } finally {
      setLoading(false);
    }
  };

  const handleJDMatch = async () => {
    if (!jobDescription) return alert("Paste job description first");
    if (!cvData || !cvData.rawText) return alert("Upload CV first");

    setLoading(true);
    try {
      const res = await matchJD(cvData.rawText, jobDescription);
      setJdScore(res.matchPercentage);
    } catch (err) {
      console.error("JD matching failed:", err);
      alert("JD matching failed");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container app-container">
      <h1 className="neon-title">CV ATS System</h1>

      {loading && (
        <div className="loading-overlay">
          <div className="spinner"></div>
          <p>Processing...</p>
        </div>
      )}

      {/* Upload */}
      <UploadCV setData={handleUploadCV} />

      {cvData && (
        <div className="glass-card">
          {/* Basic Info */}
          <div className="basic-info">
            <div><FaUser /> {cvData.name || "N/A"}</div>
            <div><FaEnvelope /> {cvData.email || "N/A"}</div>
            <div><FaPhone /> {cvData.phone || "N/A"}</div>
          </div>

          {/* Main Grid */}
          <div className="grid-layout">
            {/* Education */}
            <div className="card-section">
              <h3><FaGraduationCap /> Education</h3>
              <ul>
                {cvData.education?.length
                  ? cvData.education.map((edu, i) => (
                      <li key={i}>
                        <strong>{edu.degree}</strong> at {edu.institution} ({edu.start_date} - {edu.end_date})<br/>
                        Field: {edu.field_of_study || ""}, GPA: {edu.gpa || ""}<br/>
                        {edu.details}
                      </li>
                    ))
                  : <li>N/A</li>}
              </ul>
            </div>

            {/* Experience */}
            <div className="card-section">
              <h3><FaBriefcase /> Experience</h3>
              <ul>
                {cvData.experience?.length
                  ? cvData.experience.map((exp, i) => (
                      <li key={i}>
                        <strong>{exp.title}</strong> at {exp.company} ({exp.start_date} - {exp.end_date})<br/>
                        {exp.description}
                      </li>
                    ))
                  : <li>N/A</li>}
              </ul>
            </div>

            {/* Projects */}
            <div className="card-section">
              <h3><FaProjectDiagram /> Projects</h3>
              <ul>
                {cvData.projects?.length
                  ? cvData.projects.map((p, i) => (
                      <li key={i}>
                        <strong>{p.title}</strong>: {p.description}
                      </li>
                    ))
                  : <li>N/A</li>}
              </ul>
            </div>

            {/* Skills */}
            <div className="card-section">
              <h3><FaTools /> Skills</h3>
              <ul className="tag-list">
                {cvData.skills?.length
                  ? cvData.skills.map((s, i) => <li key={i}>{typeof s === "string" ? s : s.name}</li>)
                  : <li>N/A</li>}
              </ul>
            </div>

            {/* Certifications */}
            <div className="card-section">
              <h3><FaCertificate /> Certifications</h3>
              <ul>
                {cvData.certifications?.length
                  ? cvData.certifications.map((c, i) => <li key={i}>{typeof c === "string" ? c : c.name}</li>)
                  : <li>N/A</li>}
              </ul>
            </div>
          </div>

          {/* Highlights & Improvements */}
          <div className="glass-card analysis-wrapper">
            <div className="highlight success">
              <h3><FaCheckCircle /> Strong Highlights</h3>
              <ul>
                {cvData.highlights?.length
                  ? cvData.highlights.map((h, i) => <li key={i}>{h}</li>)
                  : <li>N/A</li>}
              </ul>
            </div>
            <div className="highlight warning">
              <h3><FaExclamationTriangle /> Needs Improvement</h3>
              <ul>
                {cvData.improvements?.length
                  ? cvData.improvements.map((i, idx) => <li key={idx}>{i}</li>)
                  : <li>N/A</li>}
              </ul>
            </div>
          </div>

          {/* ATS Score */}
          <div style={{ marginTop: "25px" }}>
            <p className="neon-text">ATS Score: {cvData.atsScore ?? "N/A"}/100</p>
          </div>

          {/* JD Match */}
          <div className="jd-box">
            <textarea
              rows="5"
              placeholder="Paste Job Description here..."
              value={jobDescription}
              onChange={(e) => setJobDescription(e.target.value)}
            />
            <button className="neon-btn" onClick={handleJDMatch}>
              Match Job Description
            </button>
            {jdScore !== null && (
              <p className="neon-text">JD Match Score: {jdScore}%</p>
            )}
          </div>
        </div>
      )}
    </div>
  );
}
